import React, { useState } from "react";
import axios from "axios";
import { Text, Button, Flex, Box } from "@chakra-ui/react";
import { EditIcon, DeleteIcon } from "@chakra-ui/icons";
import DeleteBookModal from "./DeleteBookModal";
import EditBookModal from "./EditBookModal";

interface CardProps {
  book: {
    id: number;
    name: string;
    description: string;
    author: string;
    link: string;
  };
  onEdit: () => void;
  onDelete: () => void;
}

const Card: React.FC<CardProps> = ({ book, onEdit, onDelete }) => {
  const [isEditModalOpen, setEditModalOpen] = useState(false);
  const [isDeleteModalOpen, setDeleteModalOpen] = useState(false);

  const cardStyles = {
    borderWidth: "1px",
    borderRadius: "lg",
    overflow: "hidden",
    p: "2",
  };

  const handleEditClick = () => {
    setEditModalOpen(true);
  };

  const handleDeleteClick = () => {
    setDeleteModalOpen(true);
  };

  const closeEditModal = () => {
    setEditModalOpen(false);
  };

  const closeDeleteModal = () => {
    setDeleteModalOpen(false);
  };

  const handleCreateBookmark = async () => {
    try {
      // Replace 'http://your-api-endpoint/create-bookmark' with your actual endpoint
      const response = await axios.post("http://your-api-endpoint/create-bookmark", {
        userId: 123, // Replace with the actual user ID
        dto: {
          title: book.name,
          author: book.author,
          description: book.description,
          link: book.link,
        },
      });

      console.log(response.data.message);
    } catch (error) {
      console.error("Error creating bookmark:", error);
    }
  };

  return (
    <Box {...cardStyles}>
      <Text fontSize="xl" fontWeight="bold" mb="2">
        {book.name}
      </Text>
      <Text>{book.description}</Text>
      <Flex mt="2">
        <Button onClick={handleEditClick} marginRight="2">
          <EditIcon />
        </Button>
        <Button onClick={handleDeleteClick}>
          <DeleteIcon />
        </Button>
        <Button onClick={handleCreateBookmark}>Create Bookmark</Button>
      </Flex>

      {isEditModalOpen && (
        <EditBookModal isOpen={isEditModalOpen} onClose={closeEditModal} book={book} onEdit={onEdit} />
      )}

      {isDeleteModalOpen && (
        <DeleteBookModal isOpen={isDeleteModalOpen} onClose={closeDeleteModal} onDelete={onDelete} />
      )}
    </Box>
  );
};

export default Card;
